examples.example:
l = "local variable";
log(__local);
success1 = unset(__local, "l");
success2 = unset(__local, "ll");
log(__local);

{
    l = "local variable"
}
{
    __void = null,
    success1 = true,
    success2 = false
}

g = global("global variable");
log(__global);
unset(__global, "g");
log(__global);

{
    true = true,
    false = false,
    null = null,
    g = "global variable"
}
{
    true = true,
    false = false,
    null = null
}
