examples.example:
pagecount = 1;
while(pagecount > 0) {
    pages = query("SELECT * FROM pages WHERE visited = 'n'");
    foreach(_, page, pages) {
        // ... new pages are found and added ... //
    }

    result = query("SELECT COUNT(*) FROM pages WHERE visited = 'n'");
    foreach(_, r, result) {
        pagecount = r;
    }
}
