example.code:
var list1 = [1,"a","c",2,3,"b",[1,3,2],[3,2],4,"d",{"a":2,"b":1}];
Collection.sort(list1,false,false,false); //sort normal

// Result: [[3,2],[1,3,2],{"a":2,"b":1},1,2,3,4,"a","b","c","d"]

Collection.sort(list1,true,false,false); //sort recursive

// Result: [[2,3],[1,2,3],{"b":1,"a":2},1,2,3,4,"a","b","c","d"]

var list2 = [3,"b","c",1,2,"a"];
Collection.sort(list2,false,false,false); //sort normal

// Result: [1,2,3,"a","b","c"]

Collection.sort(list2,false,false,true); //sort reverse

// Result: [3,2,1,"c","b","a"]

var obj1 = {"b":2,"c":1,"a":3};
Collection.sort(obj1,false,false,false); //sorted by value

// Result: {"c":1,"b":2,"a":3}

Collection.sort(obj1,false,true,false); //sorted by key

// Result: {"a":3,"b":2,"c":1}
