example.code:
use File, Stream, System;

var file = File.openRead("path/to/file.txt");

foreach(line in Stream.iterate(file)) {
    System.print(line);
}

use File, Stream;

var file = File.openRead("path/to/file.txt");
var splitPattern = "[^\\w]+";

foreach(word in Stream.iterate(file, splitPattern)) {
    System.print(word);
}
