use Document;

// Build the document
var doc = Document.new(
    "CustomPage",
    {
        "version": 2,
        "user": {
            "id": 54,
            "username": "lancelot34",
            "password": "IAmASafePassword"
        }
    },
    [
        {
        "version": 1,
            "user": {
                "id": 54,
                "username": "lancelot34",
                "password": "HelloWorld"
            }
        }
    ]
);

// Save the document
Document.save(doc);
