example.code:
use Mongo;
use System;
Mongo.connect("udm_default");

var contentTypes = Mongo.aggregate(
    "documents",
    [
        {
            "$group": {
                "_id": "$contenttype",
                "count": {
                    "$sum": 1
                }
            }
        }
    ]
);

foreach(result in contentTypes) {
    System.print(result._id :: ": " :: result.count);
}
