example.code:
use Mongo;
use System;
Mongo.connect("udm_default");

var pipeline = [
    {
        "$group": {
            "_id": "$contenttype",
            "count": {
                "$sum": 1
            }
        }
    }
];

var contentTypes = Mongo.aggregate("documents", pipeline);

foreach(result in contentTypes) {
    System.print(result._id :: ": " :: result.count);
}
