example.code:
use Mongo;
use System;
Mongo.connect("udm_default");

var documents = Mongo.find("documents");

foreach(document in documents) {
    System.print(document._id);
}

use Mongo;
use System;
Mongo.connect("udm_default");

var documents = Mongo.find(
    "documents",
    {
        "contenttype": "page"
    }
);

foreach(document in documents) {
    System.print(document._id);
}
