example.code:
use Mongo;
use System;
Mongo.connect("udm_default");

var document = Mongo.findOne("documents");

System.print(document);

use Mongo;
use System;
Mongo.connect("udm_default");

var document = Mongo.find(
    "documents",
    {
        "_id": "f8cbcb03-fb98-4093-8fd5-244b8667afde"
    }
);

System.print(document);

use Mongo;
use System;
Mongo.connect("udm_default");

var document = Mongo.find(
"documents", {}, {}
{
    // Positive for ascending order, negative for descending order.
    "_id": 1
}
);

System.print(document);
