example.code:
use Mongo;
use System;
Mongo.connect("udm_default");

var document = Mongo.findOne("documents");

System.print(document);

use Mongo;
use System;
Mongo.connect("udm_default");

// Sort by timestamp (newest first).
var sort = {
    "target.timestamp": -1
};

var document = Mongo.findOne("documents", {}, {}, sort);

System.print(document);
