example.code:
use Mongo;
use System;
Mongo.connect("udm_default");

var documents = Mongo.find("asset", {"_id": Mongo.objectId("567a73d9cfa9040e40700949")});

foreach(document in documents) {
    System.print(document.name);
}

use Mongo;
use System;
Mongo.connect("udm_default");

var result = Mongo.update(
    "asset",
    {"_id": Mongo.objectId("567a73d9cfa9040e40700949")},
    { "$set" :
        {"metaData.meta2":"seasonal greetings"}
    },
    {"upsert":false}
);

System.print ("Updated " :: result :: " documents.");
