example.code:
use Excel;

var workbook = Excel.loadWorkbook("workbook.xlsx");
var sheet = Excel.loadSheet(workbook, "firstsheet");

use Excel;

var workbook = Excel.loadWorkbook("workbook.xlsx");
var sheetNames = Excel.getSheetNames(workbook);
var sheet = Excel.loadSheet(workbook, sheetNames[0]);

use Excel;
use System;

var workbook = Excel.loadWorkbook("workbook.xlsx");
var sheet = Excel.loadSheet(workbook, "firstsheet");
System.print(sheet);
