example.code:
var list1 = [1,"a","c",2,3,"b",[1,3,2],[3,2],4,"d",{"a":2,"b":1}];
System.print(List.sort(list1,false,false,false)); //sort normal
System.print(List.sort(list1,true,false,false)); //sort recursive

var list2 = [3,"b","c",1,2,"a"];
System.print(List.sort(list2,false,false,false)); //sort normal
System.print(List.sort(list2,false,false,true)); //sort reverse

var obj1 = {"b":2,"c":1,"a":3};
System.print(List.sort(obj1,false,false,false)); //sorted by value
System.print(List.sort(obj1,false,true,false)); //sorted by key
