examples.example:
//An example of argument indices to re-order input
String.format("%3$2s %1$2s %1$2s %2$2s" , ["a", "b", "c"] );
//An example of setting the width and precision of an argument.
var v = 0.1234567891;
String.format("value = %+10.4f or value = %010.7f or value = %-8.1f or  value= %+.3f" , [v, v , v, -v]);

    	//The example of argument indices to re-order input:
		"c a a b"
		//The example of setting the width and precision of an argument.
		"value =        +0.1235 or value =  00.1234568 or value = 0.1          value = -0.123'
