example.code:
use Web;
use System;

var page = Web.loadPage("http://www.xillio.com");
System.print(Web.getText(page));

use Web;
use System;

var page = Web.loadPage("http://www.xillio.com");

var text = "";

if (page != null) {
    var header = Web.xPath (page, "//h1");
    var text = Web.getText(header);
    System.print(text);
} else {
    System.print("no page");
}

use Web;
use System;
use XML;

var page = Web.loadPage("http://www.w3schools.com/xml/cd_catalog.xml"); // Load the web page with XML content
var xml_source = Web.getSource(page); // Get the XML source (i.e. page source)

var xml = XML.fromString(xml_source); // Parse XML source using XML plugin

System.print(XML.getText(xml));
