example.code:
use Web;
use System;

var page = Web.loadPage("http://www.xillio.com/", {"enablejs":true, "loadimages":false});
var mainmenutitles = Web.xPath(page, "//ul[@id='nav']/li/a/text()");
System.print(mainmenutitles);
var mainmenuurls = Web.xPath(page, "//ul[@id='nav']/li/a[@href]");
System.print(mainmenuurls);

/*
Options that can be passed to the loadPage function:
proxyhost              hostname or ip (required for using a proxy)
proxyport              hostport (required for using a proxy)
proxyuser              username (optional)
proxypass              password (optional)
proxytype              [http,socks5] (default: http)
enablejs               [true,false] (default: true)
enablewebsecurity      [true,false] (default: true)
loadimages             [true,false] (default: true)
insecuressl            [true,false] (default:false)
sslprotocol            [sslv3,sslv2,tlsv1,any] (default:sslv3)
user                   username (required for HTTP authentication)
pass                   password (required for HTTP authentication)
timeout                timeoutvalue (in milliseconds)
*/

/* Contents of the select.html



            Volvo
            Saab
            Mercedes
            Audi



*/

var page = Web.loadPage("file:///C:/tmp/select.html");
var selectbox = Web.xPath(page, "//select");
var choice = Web.xPath(page, "//option[@value='saab']");
var before = Web.selected(choice);
Web.select(choice, true);
var after = Web.selected(choice);
System.print(before);
System.print(after);
